css:
atom-text-editor.editor .debug-breakpoint,
atom-text-editor.editor .debug-breakpoint-hint {
  width: 1em;
  text-align: center;
}
atom-text-editor.editor .debug-breakpoint:after,
atom-text-editor.editor .debug-breakpoint-hint:after {
  display: inline-block;
  vertical-align: middle;
  width: 0.9em;
  height: 0.9em;
  content: "";
  background: #d13b2e;
  border-radius: 50%;
  margin-top: -2px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-sizing: border-box;
}
atom-text-editor.editor .debug-breakpoint-hint {
  opacity: 0.4;
}
atom-text-editor.editor .gutter[gutter-name="debug-gutter"] {
  cursor: pointer;
}
atom-text-editor.editor .line-number.debug-position,
atom-text-editor.editor .line-number.debug-position-error {
  position: relative;
  color: #e2c08d;
  overflow: hidden;
  border-radius: 3px;
}
atom-text-editor.editor .line-number.debug-position:after,
atom-text-editor.editor .line-number.debug-position-error:after {
  content: "";
  position: absolute;
  top: 50%;
  right: 0px;
  margin-top: -100px;
  border-left: 100px solid rgba(173, 124, 11, 0.5);
  border-top: 100px solid transparent;
  border-bottom: 100px solid transparent;
}
atom-text-editor.editor .line-number.debug-position-error {
  color: #ff6347;
}
atom-text-editor.editor .line-number.debug-position-error:after {
  border-left: 100px solid rgba(209, 59, 46, 0.5);
}
atom-text-editor.editor .line.debug-position,
atom-text-editor.editor .line.debug-position-error {
  border-radius: 3px;
  background: rgba(173, 124, 11, 0.2);
}
atom-text-editor.editor .line.debug-position-error {
  background: rgba(209, 59, 46, 0.2);
}
.debug-toolbar {
  padding: 5px;
  -webkit-user-select: none;
}
.debug-toolbar .btn-toolbar {
  margin-left: 0;
  margin-right: 0;
}
.debug-toolbar .btn-toolbar .btn .icon {
  display: inline-block;
  width: 17px;
  height: 19px;
  fill: currentColor;
  vertical-align: middle;
  margin-top: -2px;
}
.debug-toolbar .btn-toolbar .options {
  float: right;
  margin-right: 0;
}
.debug-sidebar {
  display: flex;
  flex-direction: column;
  -webkit-user-select: none;
}
.debug-sidebar .selectable {
  -webkit-user-select: text;
}
.debug-sidebar.with-toolbar {
  background: rgba(0, 0, 0, 0.2);
}
.debug-sidebar.with-toolbar > .body {
  padding-top: 10px;
}
.debug-sidebar > .body {
  flex: 1 1;
  overflow: auto;
  padding-left: 10px;
  padding-right: 10px;
}
.debug-sidebar > .body > ul {
  width: min-content;
}
.debug-sidebar > .options {
  flex: 0 0 auto;
  margin: 10px;
}
.debug-sidebar .toolbar {
  display: flex;
  flex: 0 0 auto;
  background: #21252b;
  padding: 10px;
  border-bottom: 1px solid #181a1f;
}
.debug-sidebar .toolbar > .spacer {
  flex: 1 1;
}
.debug-sidebar .toolbar > .btn-group {
  display: block;
  margin-right: 10px;
}
.debug-sidebar .toolbar > .btn-group > .btn {
  height: 100%;
}
.debug-sidebar .inset-panel.variables {
  flex: 2 0;
}
.debug-sidebar .inset-panel + .inset-panel {
  margin-top: 10px;
}
.debug-sidebar .inset-panel .panel-heading {
  cursor: default;
}
.debug-sidebar .inset-panel .panel-heading .btn {
  top: 0;
  margin: -4px -4px -4px 0;
}
.debug-sidebar .inset-panel .panel-heading .options {
  float: right;
}
.debug-sidebar .inset-panel .panel-body {
  overflow: auto;
  position: relative;
}
.debug-sidebar .list-item {
  overflow: hidden;
  text-overflow: ellipsis;
}
.debug-sidebar .variable-list .type {
  display: none;
}
.debug-sidebar .variable-list.show-types .type {
  display: inline;
}
.debug-sidebar table {
  width: 100%;
  margin: 0;
  line-height: 25px;
  white-space: nowrap;
  overflow: hidden;
  cursor: default;
}
.debug-sidebar table tr.selected > td:first-child {
  width: 100%;
}
.debug-sidebar table tr.selected > td::before {
  content: '';
  background-color: #31363f;
  position: absolute;
  left: 0;
  right: 0;
  height: 25px;
  z-index: 0;
}
.debug-sidebar table tr > td > * {
  position: relative;
  z-index: 1;
}
.debug-sidebar table .no-icon {
  display: inline-block;
  padding-left: 21px;
}
.debug-sidebar code {
  font-family: Menlo, Consolas, 'DejaVu Sans Mono', monospace;
  font-size: 12px;
  color: #ffffff;
  background: transparent;
}
.debug-sidebar code .value {
  color: #6494ed;
}
.debug-custom-panel .panel-heading {
  position: relative;
  cursor: default;
}
.debug-custom-panel .panel-heading .action-close {
  border: 0 none;
  background: transparent;
  position: absolute;
  top: 0;
  right: 0;
  height: 100%;
  cursor: pointer;
}
.debug-custom-panel .panel-heading .action-close::before {
  width: 28px;
  margin-right: 0;
  text-align: center;
  vertical-align: middle;
}
.debug-custom-panel .panel-heading .action-close::after {
  content: '';
  display: inline-block;
  height: 100%;
  vertical-align: middle;
}
.debug-custom-panel .header {
  padding: 2.5px 5px;
  min-width: 200px;
}
.debug-custom-panel .header-item {
  margin: 2.5px 0;
}
.debug-custom-panel .btn-group {
  display: flex;
  flex: 1 1 150px;
  min-width: 150px;
}
.debug-custom-panel .btn-item {
  flex: auto;
}
.debug-custom-panel .input-block {
  display: flex;
  flex-wrap: wrap;
  width: 100%;
  min-width: 200px;
}
.debug-custom-panel .body {
  display: flex;
}
.debug-custom-panel .labeled-block {
  align-items: center;
}
.debug-custom-panel .labeled-block label {
  margin-top: 0;
  margin-bottom: 0;
}
.debug-custom-panel .input-select-item {
  padding-left: 5px;
  margin-left: 10px;
}
.debug-custom-panel .input-inline-block {
  display: inline-block;
  flex: auto;
}
.debug-custom-panel .inline-block-start {
  display: flex;
  flex: none;
  flex-direction: column;
  padding: 5px;
}
.debug-custom-panel .inline-block-start > * {
  margin: 5px 0;
  flex: 0 1 auto;
  line-height: 2.3em;
}
.debug-custom-panel .inline-block-start > *.btn-lg {
  flex: 1 1 auto;
  line-height: 2em;
}
.debug-custom-panel .inline-block-start > *:first-child {
  margin-top: 0;
}
.debug-custom-panel .inline-block-start > *:last-child {
  margin-bottom: 0;
}
.debug-custom-panel .input-block-item {
  flex: 1;
  padding: 5px;
}
.debug-custom-panel .input-block-item--flex {
  flex: 100 1 260px;
}
.debug-custom-panel .editor-container {
  position: relative;
}
.debug-custom-panel .editor-container atom-text-editor {
  width: 100%;
}
.debug-variable-tooltip {
  max-height: 300px;
  overflow: hidden;
  line-height: 1.2em;
  text-align: left;
  text-overflow: ellipsis;
}
.debug-hint-block {
  cursor: default;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.debug-hint-block.hidden {
  display: none;
}
.debug-hint-block .indent {
  display: inline-block;
  white-space: pre;
}
.debug-hint-block .content {
  position: relative;
  margin-top: 8px;
  margin-bottom: 4px;
  display: inline-block;
  font-size: 90%;
  color: #6494ed;
  background: rgba(100, 148, 237, 0.1);
  border-radius: 3px;
  border-top-left-radius: 0;
  padding: 0 4px;
}
.debug-hint-block .content:before {
  display: block;
  position: absolute;
  top: -8px;
  left: 0;
  content: ' ';
  border-right: 8px solid transparent;
  border-bottom: 8px solid rgba(100, 148, 237, 0.1);
}
.debug-hint-overlay {
  position: relative;
  left: 1em;
  margin-top: -1px;
  max-width: 400px;
  cursor: default;
  border-radius: 3px;
  padding: 0 4px 1px;
  overflow: hidden;
  text-overflow: ellipsis;
  color: #ffffff;
  background: #0f82e6;
  border-top: 1px solid #198cf0;
  border-bottom: 1px solid #0d77d3;
  box-shadow: 0 1px 5px rgba(0, 0, 0, 0.3);
}
.debug-fadein {
  animation: debug-fadein 2s;
}
@keyframes debug-fadein {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}
